        public static bool CheckNumberPassword(string password)
        {
            if (password == "")
                return false;
            foreach (char c in password)
                if (!((byte)c >= 48 && (byte)c <= 57))
                    return false;
            return true;
        }
        public static bool InvalidCharacters(string Name)
        {


            if (Name.Length > 16)
                Name = Name.Substring(0, 16);
            if (Name == "")
                return false;
            foreach (char c in Name)
                if (Conquer_Online_Server.Kernel.InvalidCharacters.Contains(c) || (byte)c < 48)
                    return true;
            return true;


        }

        public static bool InvalidCharacters2(string Name)
        {
            using (var rdr = new MySqlReader(new MySqlCommand(MySqlCommandType.SELECT).Select("entities").Where("name", Name)))
                return rdr.Read();
        }
        public static bool InvalidCharacters3(string Name)
        {
            var rdr = new Conquer_Online_Server.Database.MySqlReader(new MySqlCommand(MySqlCommandType.SELECT).Select("entities").Where("Name", Name));
            if (rdr.Read())
            {
                rdr.Close();
                //r.Close();
                rdr.Dispose();

                return false;

            }
            rdr.Close();
            rdr.Dispose();
            return true;


        }
